INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (1, 'Abraham Lincoln Birthplace', 'Hodgenville', 'For over a century people from around the world have come to rural Central Kentucky to honor the humble beginnings of our 16th president, Abraham Lincoln. His early life on Kentucky''s frontier shaped his character and prepared him to lead the nation through Civil War. Visit our country''s first memorial to Lincoln, built with donations from young and old, and the site of his childhood home.', 18, 'https://www.nps.gov/abli/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (2, 'Acadia', 'Bar Harbor', 'Acadia National Park protects the natural beauty of the highest rocky headlands along the Atlantic coastline of the United States, an abundance of habitats, and a rich cultural heritage. At 4 million visits a year, it''s one of the top 10 most-visited national parks in the United States. Visitors enjoy 27 miles of historic motor roads, 158 miles of hiking trails, and 45 miles of carriage roads.', 20, 'https://www.nps.gov/acad/index.htm');
