INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (3, 'Adams', 'Quincy', 'From the sweet little farm at the foot of Penn’s Hill to the gentleman’s country estate at Peace field, Adams National Historical Park is the story of “heroes, statesman, philosophers … and learned women” whose ideas and actions helped to transform thirteen disparate colonies into one united nation.', 22, 'https://www.nps.gov/adam/index.htm');
