INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (27, 'Arkansas Post', 'Gillett', 'Located at the confluence of two rivers, Arkansas Post has served as a gathering place for many cultures throughout human history - it represents cultural cooperation, conflict, synthesis, and diversity.', 4, 'https://www.nps.gov/arpo/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (26, 'Arlington House, The Robert E. Lee Memorial', 'Arlington', 'Arlington House is the nation’s memorial to Robert E. Lee. It honors him for specific reasons, including his role in promoting peace and reunion after the Civil War. In a larger sense it exists as a place of study and contemplation of the meaning of some of the most difficult aspects of American history: military service; sacrifice; citizenship; duty; loyalty; slavery and freedom.', 49, 'https://www.nps.gov/arho/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (28, 'Assateague Island', 'Berlin', 'Want to live on the edge? Visit a place recreated each day by ocean wind and waves. Life on Assateague Island has adapted to an existence on the move. Explore sandy beaches, salt marshes, maritime forests and coastal bays. Rest, relax, recreate and enjoy some time on the edge of the continent.', 21, 'https://www.nps.gov/asis/index.htm');
