INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (32, 'Baltimore-Washington', 'Greenbelt', 'This 29-mile highway connects Baltimore, MD and Washington, D.C. The parkway has carried visitors to and from the capital city since 1954.', 21, 'https://www.nps.gov/bawa/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (31, 'Bandelier', 'Los Alamos', 'Bandelier National Monument protects over 33,000 acres of ruggedly beautiful canyon and mesa country. Petroglyphs, dwellings carved into the soft rock cliffs, and standing masonry walls pay tribute to the early days of a culture that still survives in the surrounding communities.', 32, 'https://www.nps.gov/band/index.htm');
