INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (35, 'Belmont-Paul Women''s Equality', 'Washington', 'Home to the National Woman''s Party for more than 90 years, this was the epicenter of the struggle for women''s rights. From this house in the shadow of the U.S. Capitol and Supreme Court, Alice Paul and the NWP developed innovative strategies and tactics to advocate for the Equal Rights Amendment and equality for women. President Barack Obama designated the national monument on April 12, 2016.', 9, 'https://www.nps.gov/bepa/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (34, 'Bent''s Old Fort', 'La Junta', 'In the 1840s the Arkansas River was the border between territory claimed by the United States and Mexico. Located on the river, Bent''s Fort was an adobe trading post on the Santa Fe Trail, where traders, trappers, travelers, and the Cheyenne and Arapaho tribes came together in peaceful terms for trade. Step back in time to experience the sights, sounds, and smells of the past.', 6, 'https://www.nps.gov/beol/index.htm');
