INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (33, 'Bering Land Bridge', 'Nome', 'Bering Land Bridge National Preserve lies at the continental crossroad that greatly influenced the distribution of life in the Western Hemisphere during the Pleistocene Epoch. It is a vital landscape for Indigenous communities who depend on the land just as their ancestors did for many generations. It is a wild and ecologically healthy landscape unlike any other.', 2, 'https://www.nps.gov/bela/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (36, 'Big Bend', 'Big Bend National Park', 'There is a place in Far West Texas where night skies are dark as coal and rivers carve temple-like canyons in ancient limestone. Here, at the end of the road, hundreds of bird species take refuge in a solitary mountain range surrounded by weather-beaten desert. Tenacious cactus bloom in sublime southwestern sun, and species diversity is the best in the country. This magical place is Big Bend...', 45, 'https://www.nps.gov/bibe/index.htm');
