INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (62, 'Cape Cod', 'Eastham', 'The great Outer Beach described by Thoreau in the 1800s is protected within the national seashore. Forty miles of pristine sandy beach, marshes, ponds, and uplands support diverse species. Lighthouses, cultural landscapes, and wild cranberry bogs offer a glimpse of Cape Cod''s past and continuing ways of life. Swimming beaches and walking and biking trails beckon today''s visitors.', 22, 'https://www.nps.gov/caco/index.htm');
