INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (66, 'Captain John Smith Chesapeake', 'Jamestown', 'People first arrived in the Chesapeake Bay during the last ice age. As glaciers melted, diverse societies learned to thrive in a world of water. When Englishman Captain John Smith explored the Bay in 1608, he documented hundreds of American Indian communities. Today, sites on his map are archeological treasures and sacred sites for tribal citizens. Come join us on the shores of the Chesapeake Bay!', 49, 'https://www.nps.gov/cajo/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (81, 'Capulin Volcano', 'Capulin', 'Part of the 8,000 square mile Raton-Clayton Volcanic Field, Capulin Volcano showcases the volcanic geology of northeastern New Mexico. The views are spectacular day or night, with views of 4 different states from the volcanic rim and one of the darkest night skies in the country. Whether it''s a quick stop or a day''s trip, enjoy exploring the landscape of this unique volcano!', 32, 'https://www.nps.gov/cavo/index.htm');
