INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (77, 'Carl Sandburg Home', 'Flat Rock', '“I make it clear why I write as I do and why other poets write as they do. After hundreds of experiments I decided to go my own way in style and see what would happen.” Carl Sandburg''s free verse style of poetry, journalism, biography, children''s stories, prose writing, and social activism provided a popular voice for the American people of the twentieth century.', 34, 'https://www.nps.gov/carl/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (80, 'Carlsbad Caverns', 'Carlsbad', 'High ancient sea ledges, deep rocky canyons, flowering cactus, and desert wildlife—treasures above the ground in the Chihuahuan Desert. Hidden beneath the surface are more than 119 caves—formed when sulfuric acid dissolved limestone leaving behind caverns of all sizes.', 32, 'https://www.nps.gov/cave/index.htm');
