INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (84, 'Cedar Creek & Belle Grove', 'Middletown', 'Virginia’s Shenandoah Valley has a long and rich heritage that still inspires today. Native Americans first shaped the land, followed by colonists and settlers. The fertile valley became an important wheat-growing region in the 1800s. It also witnessed dramatic Civil War campaigns, including the Battle of Cedar Creek, a decisive Federal victory that helped bring the war to an end.', 49, 'https://www.nps.gov/cebe/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (90, 'Chaco Culture', 'Nageezi', 'Today the massive buildings of the Ancestral Puebloan people still testify to the organizational and engineering abilities not seen anywhere else in the American Southwest. For a deeper contact with the canyon that was central to thousands of people between 850 and 1250 CE, come and explore Chaco through guided tours, hiking & biking trails, evening campfire talks, and night sky programs.', 32, 'https://www.nps.gov/chcu/index.htm');
