INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (94, 'Chesapeake & Ohio Canal', 'Williamsport', 'Preserving America''s early transportation history, the C&O Canal began as a dream of passage to Western wealth. Operating for nearly 100 years the canal was a lifeline for communities along the Potomac River as coal, lumber, and agricultural products floated down the waterway to market. Today it endures as a pathway for discovering historical, natural, and recreational treasures.', 21, 'https://www.nps.gov/choh/index.htm');
