INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (83, 'Chesapeake Bay', 'Annapolis', 'Over 41 million acres are connected through the waters that flow to and through Chesapeake Bay. The National Park Service strives to connect people to the opportunities and adventures throughout the watershed. Inspiring stewardship across this vast region supports recreation, business, tourism, learning, and a thriving natural resource for all to enjoy.', 21, 'https://www.nps.gov/cbpo/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (89, 'Chickamauga & Chattanooga', 'Fort Oglethorpe', 'In 1863, Union and Confederate forces fought for control of Chattanooga, known as the "Gateway to the Deep South." The Confederates were victorious at nearby Chickamauga in September. However, renewed fighting in Chattanooga that November provided Union troops victory and control of the city. After the fighting, a Confederate soldier ominously wrote, "This...is the death-knell of the Confederacy."', 11, 'https://www.nps.gov/chch/index.htm');
