INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (91, 'Chickasaw', 'Sulphur', 'Springs, streams, lakes - whatever its form, water is the attraction at Chickasaw National Recreation Area.', 37, 'https://www.nps.gov/chic/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (92, 'Chiricahua', 'Willcox', 'A "Wonderland of Rocks" is waiting for you to explore at Chiricahua National Monument. The 8-mile paved scenic drive and 17-miles of day-use hiking trails provide opportunities to discover the beauty, natural sounds, and inhabitants of this 12,025 acre site. Visit the Faraway Ranch Historic District to discover more about the people who have called this area home.', 3, 'https://www.nps.gov/chir/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (96, 'Christiansted', 'Christiansted', 'Christiansted National Historic Site was established in 1952 as the first unit of the National Park Service in the Virgin Islands of the United States. Covering over seven acres the park tells the stories about Danish economy and ways of life, and the interactions of people in Europe, Africa, West Indies, and North America during the Danish era of occupation of the Virgin Islands (1734-1917).', 47, 'https://www.nps.gov/chri/index.htm');
INSERT INTO park_fee (park_id, name, cost_cents) VALUES (96, 'Entrance - Per Person', 1000);
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (99, 'City Of Rocks', 'Almo', 'Emigrants of the California Trail describe the rocks here in vivid detail as "a city of tall spires,” “steeple rocks," and "the silent city." Today, this backcountry byway attracts rock climbers, campers, hikers, hunters, and those with the spirit of adventure. There''s inspirational scenery, exceptional opportunities for geologic study, and remnants of the Old West awaiting your discovery.', 13, 'https://www.nps.gov/ciro/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (114, 'Civil War Defenses of Washington', 'Washington', 'On forested hills surrounding the nation''s capital are the remnants of a complex system of Civil War fortifications. These strategic buttresses transformed the young capital into one of the world''s most fortified cities. By 1865, 68 forts and 93 batteries armed with over 800 cannons encircled Washington, DC. Today, you can visit 17 of the original sites now managed by the National Park Service.', 9, 'https://www.nps.gov/cwdw/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (100, 'Clara Barton', 'Glen Echo', 'Clara Barton dedicated her life and energies to help others in times of need - both home and abroad, in peacetime as well as during military emergencies. Glen Echo was her home the last 15 years of her life, and the structure illustrates her dedication and concern for those less fortunate than herself. Open for first floor tours Fridays, Saturdays, and Sundays at 1:00, 2:00, 3:00, & 4:00 pm.', 21, 'https://www.nps.gov/clba/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (103, 'Colonial', 'Yorktown', 'On May 13, 1607, Jamestown was established as the first permanent English settlement in North America. Three cultures came together – European, Virginia Indian and African–to create a new society that would eventually seek independence from Great Britain. On October 19, 1781, American and French troops defeated the British at Yorktown in the last major battle of the American Revolutionary War.', 49, 'https://www.nps.gov/colo/index.htm');
