INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (102, 'Colorado', 'Fruita', 'Colorado National Monument preserves one of the grand landscapes of the American West. But this treasure is much more than a monument. Towering monoliths exist within a vast plateau and canyon panorama. You can experience sheer-walled, red rock canyons along the twists and turns of Rim Rock Drive, where you may spy bighorn sheep and soaring eagles.', 6, 'https://www.nps.gov/colm/index.htm');
