INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (109, 'Craters Of The Moon', 'Arco', 'Craters of the Moon is a vast ocean of lava flows with scattered islands of cinder cones and sagebrush. We invite you to explore this "weird and scenic landscape" where yesterday''s volcanic events are likely to continue tomorrow.', 13, 'https://www.nps.gov/crmo/index.htm');
