INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (122, 'Delaware Water Gap', 'Bushkill', 'Situated within the most densely populated region of the United States, Delaware Water Gap National Recreation Area provides a unique opportunity to experience tranquil landscapes, rich human history, and striking scenery along 40 miles of the longest free-flowing river east of the Mississippi. The park offers year-round recreation including hiking, paddling, fishing, and hunting.', 39, 'https://www.nps.gov/dewa/index.htm');
