INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (420, 'Emmett Till and Mamie Till-Mobley', 'Sumner', 'In 1955, 14-year-old Emmett Till traveled to Money, Mississippi, to visit relatives. He was kidnapped, tortured, and murdered after reportedly whistling at a white woman. His mother, Mamie Till-Mobley, insisted on an open-casket funeral near their hometown of Chicago. Her brave decision let the world see the racist violence inflicted upon her son and set the Civil Rights Movement into motion.', 25, 'https://www.nps.gov/till/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (136, 'Eugene O''Neill', 'Danville', 'America''s only Nobel Prize winning playwright, Eugene O''Neill, chose to live in Northern California at the height of his writing career. Isolated from the world and within the walls of his home, O''Neill wrote his final and most memorable plays; The Iceman Cometh, Long Day''s Journey Into Night, and A Moon for the Misbegotten.', 5, 'https://www.nps.gov/euon/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (137, 'Everglades', 'Homestead', 'Everglades National Park protects an unparalleled landscape that provides important habitat for numerous rare and endangered species like the manatee, American crocodile, and the elusive Florida panther. An international treasure as well - a World Heritage Site, International Biosphere Reserve, a Wetland of International Importance, and a specially protected area under the Cartagena Treaty.', 10, 'https://www.nps.gov/ever/index.htm');
