INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (185, 'Glen Canyon', 'Page', 'Encompassing over 1.25 million acres, Glen Canyon National Recreation Area offers unparalleled opportunities for water-based & backcountry recreation. The recreation area stretches for hundreds of miles from Lees Ferry in Arizona to the Orange Cliffs of southern Utah, encompassing scenic vistas, geologic wonders, and a vast panorama of human history.', 3, 'https://www.nps.gov/glca/index.htm');
