INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (187, 'Glen Echo', 'Glen Echo', 'Glen Echo Park began in 1891 as a National Chautauqua Assembly "to promote liberal and practical education." By 1911, it transformed into DC''s premier amusement park until it closed in 1968. Since 1971, the National Park Service has owned and operated the site and today, with the help of the Glen Echo Park Partnership for Arts and Culture, offers year-round cultural and recreational activities.', 21, 'https://www.nps.gov/glec/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (186, 'Gloria Dei Church', 'Philadelphia', 'Before Pennsylvania there was New Sweden. Discover this last historic link to a forgotten past. Visit the church''s pastoral surroundings and its burial ground of patriots and ordinary citizens alike.', 39, 'https://www.nps.gov/glde/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (188, 'Golden Gate', 'San Francisco', 'Experience a park so rich it supports 19 distinct ecosystems with over 2,000 plant and animal species. Go for a hike, enjoy a vista, have a picnic or learn about the centuries of overlapping history from California’s indigenous cultures, Spanish colonialism, the Mexican Republic, US military expansion and the growth of San Francisco. All of this and more awaits you, so get out and find your park.', 5, 'https://www.nps.gov/goga/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (190, 'Golden Spike', 'Promontory, Utah', 'One of the greatest technological achievements of the 19th century is the completion of the first transcontinental railroad across the United States. Visitors to the park can see the location of the Last Spike Site, 1869 railroad construction features, walk or drive on the original railroad grade, and get an up close view of Victorian era replica locomotives.', 46, 'https://www.nps.gov/gosp/index.htm');
