INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (189, 'Governors Island', 'New York', 'From 1794 to 1966, the U.S. Army on Governors Island was part of the social, political, and economic tapestry of New York City. Today, the island is a vibrant summer seasonal venue of art, culture, and performance against the backdrop of two centuries of military heritage and the skyline of one of the great cities of the world. The island is open to the public. Check Alerts', 33, 'https://www.nps.gov/gois/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (192, 'Grand Canyon', 'Grand Canyon', 'Grand Canyon National Park, in Northern Arizona, encompasses 278 miles (447 km) of the Colorado River and adjacent uplands. Located on ancestral homeland of 11 Associated Tribes, Grand Canyon is one of the most spectacular examples of erosion anywhere in the world—unmatched in the incomparable vistas it offers visitors from the rims. The South Rim is open. The North Rim is CLOSED for the winter.', 3, 'https://www.nps.gov/grca/index.htm');
