INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (198, 'Great Sand Dunes', 'Mosca', 'Open 24/7 year round! There are no limitations, timed entries, or reservations to visit. The tallest dunes in North America are the centerpiece in a diverse landscape of grasslands, wetlands, forests, alpine lakes, and tundra. Stay on a moonless night to experience countless stars in this International Dark Sky Park!', 6, 'https://www.nps.gov/grsa/index.htm');
