INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (202, 'Guilford Courthouse', 'Greensboro', '"I never saw such fighting since God made me. The Americans fought like demons." - Lt. General Charles, Earl Cornwallis On March 15, 1781, six years into the American Revolution, General Greene and Lord Cornwallis'' troops faced off at a small courthouse community. The battle would change the course of the Southern Campaign of the American Revolution.', 34, 'https://www.nps.gov/guco/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (203, 'Gulf Islands', 'Gulf Breeze', 'Millions of visitors are drawn to the Gulf of Mexico for Gulf Islands National Seashore''s emerald coast waters, magnificent white beaches, fertile marshes and historical landscapes. Come explore with us today!', 10, 'https://www.nps.gov/guis/index.htm');
