INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (263, 'Lake Meredith', 'Fritch', 'Within the dry plains of the Texas Panhandle lies a hidden oasis, a haven where wildlife and people can find relief from the dry grasslands above. Through this plain, the Canadian River has cut dramatic 200-foot canyons, or breaks, where humans lived many years ago. Lake Meredith occupies these hidden coves and provides 7 different habitats for migratory birds and other wildlife.', 45, 'https://www.nps.gov/lamr/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (264, 'Lake Roosevelt', 'Coulee Dam', 'The ancient geologic landscape of the upper Columbia River cradles Lake Roosevelt in walls of stone carved by massive ice age floods. Come explore the shorelines and learn the stories of American Indians, traders and trappers, settlers and dam builders who called this place home. Swim, boat, hike, camp, and fish at this hidden gem in Northeast Washington, created by the Grand Coulee Dam.', 50, 'https://www.nps.gov/laro/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (265, 'Lassen Volcanic', 'Mineral', 'Lassen Volcanic National Park is home to steaming fumaroles, meadows freckled with wildflowers, clear mountain lakes, and numerous volcanoes. Jagged peaks tell the story of its eruptive past while hot water continues to shape the land.', 5, 'https://www.nps.gov/lavo/index.htm');
