INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (260, 'Lava Beds', 'Tulelake', 'Lava Beds National Monument is a land of turmoil, both geological and historical. Over the last half-million years, volcanic eruptions on the Medicine Lake shield volcano have created a rugged landscape dotted with diverse volcanic features. More than 800 caves, Native American rock art sites, historic battlefields and campsites, and a high desert wilderness experience await you!', 5, 'https://www.nps.gov/labe/index.htm');
