INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (290, 'Mesa Verde', 'Mesa Verde National Park', 'For over 700 years, the Ancestral Pueblo people built thriving communities on the mesas and in the cliffs of Mesa Verde. Today, the park protects the rich cultural heritage of 26 Pueblos and Tribes and offers visitors a spectacular window into the past. This World Heritage Site and International Dark Sky Park is home to over a thousand species, including several that live nowhere else on earth.', 6, 'https://www.nps.gov/meve/index.htm');
