INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (332, 'Oregon Caves', 'Cave Junction', 'Deep within the Siskiyou Mountains are dark, twisting passages that await your discovery. Eons of acidic water seeping into marble rock created and decorated the wondrous “Marble Halls of Oregon.” Join a tour, get a taste of what caving is all about, and explore a mountain from the inside and out!', 38, 'https://www.nps.gov/orca/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (333, 'Oregon', 'Santa Fe', 'Imagine yourself an emigrant headed for Oregon: would promises of lush farmlands and a new beginning lure you to leave home and walk for weeks? More than 2,000 miles of trail ruts and traces can still be seen along the Oregon National Historic Trail in six states and serve as reminders of the sacrifices, struggles, and triumphs of early American settlers.', 32, 'https://www.nps.gov/oreg/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (334, 'Organ Pipe Cactus', 'Ajo', 'Look closely. Look again. The sights and sounds of Organ Pipe Cactus National Monument, an International Biosphere Reserve, reveal a thriving community of plants and animals. Human stories echo throughout the Sonoran Desert, chronicling thousands of years of desert living. A wilderness hike, a scenic drive, or a night of camping will expose you to a living desert abounding with hidden life.', 3, 'https://www.nps.gov/orpi/index.htm');
