INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (347, 'Petroglyph', 'Albuquerque', 'Petroglyph National Monument protects one of the largest petroglyph sites in North America, featuring designs and symbols carved onto volcanic rocks by Native Americans and Spanish settlers 400 to 700 years ago. These images are a valuable record of cultural expression and hold profound spiritual significance for contemporary Native Americans and for the descendants of the early Spanish settlers.', 32, 'https://www.nps.gov/petr/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (352, 'Pictured Rocks', 'Munising', 'Sandstone cliffs, beaches, waterfalls, sand dunes, inland lakes, deep forest, and wild shoreline beckon you to visit Pictured Rocks National Lakeshore. The power of Lake Superior shapes the park''s coastal features and affects every ecosystem, creating a unique landscape to explore. Hiking, camping, sightseeing, and four-season outdoor opportunities abound.', 23, 'https://www.nps.gov/piro/index.htm');
