INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (354, 'Pipe Spring', 'Fredonia', 'The rich history of Pipe Spring and its flowing water comes alive as you explore the traditions of the Kaibab Paiute and the Mormon settlers through the museum, historic fort, cabins, and garden. Hike the Ridge Trail to enjoy geologic wonders, plants, and wildlife. Attend living history demonstrations and talks and be sure to visit with our amazing ranch animals!', 3, 'https://www.nps.gov/pisp/index.htm');
