INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (398, 'Shenandoah', 'Luray', 'Just 75 miles from the bustle of Washington, D.C., Shenandoah National Park is a land bursting with cascading waterfalls, spectacular vistas, fields of wildflowers, and quiet wooded hollows. With over 200,000 acres of protected lands that are haven to deer, songbirds, and black bear, there''s so much to explore...and your journey begins right here!', 49, 'https://www.nps.gov/shen/index.htm');
