INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (413, 'Thomas Jefferson Memorial', 'Washington', 'Author of the Declaration of Independence, statesman and visionary for the founding of a nation.', 9, 'https://www.nps.gov/thje/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (418, 'Thomas Stone', 'Port Tobacco', 'Choosing revolution, as Thomas Stone and many others eventually did, was not an easy or inevitable decision for most American colonists. The outcome of a war with England was far from certain and regardless of who won, the lives of the colonists would never be the same. With safety, security, family and fortune at stake, courage and personal sacrifice were tested no matter what side was chosen.', 21, 'https://www.nps.gov/thst/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (419, 'Timpanogos Cave', 'American Fork', 'The caves are now closed for the winter. Hike your way past stunning vistas to explore a hidden underground world. Taste the thrill of caving as you twist and bend to enter beautifully decorated rooms. Learn the science behind formations and hear stories of cave exploration and preservation. Experience and discover as you go – geologic mysteries await.', 46, 'https://www.nps.gov/tica/index.htm');
