INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (421, 'Timucuan', 'Jacksonville', 'Visit one of the last unspoiled coastal wetlands on the Atlantic Coast. Discover 6,000 years of human history and experience the beauty of salt marshes, coastal dunes, and hardwood hammocks. The Timucuan Preserve includes Fort Caroline and Kingsley Plantation.', 10, 'https://www.nps.gov/timu/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (422, 'Tonto', 'Roosevelt', 'The Salado Phenomena, 700 years ago, blended ideas of neighboring Native American cultures to emerge a unique and vibrant society. Tonto National Monument showcases two Salado-style cliff dwellings. Colorful pottery, woven cotton cloth, and other artifacts tell a story of people living and using resources from the northern Sonoran Desert from 1250 to 1450 CE.', 3, 'https://www.nps.gov/tont/index.htm');
