INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (441, 'Vietnam Veterans Memorial', 'Washington', 'Honoring the men and women who served in the controversial Vietnam War, the Vietnam Veterans Memorial chronologically lists the names of 58,318 Americans who gave their lives in service to their country.', 9, 'https://www.nps.gov/vive/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (439, 'Virgin Islands Coral Reef', 'St. John', 'Virgin Islands Coral Reef National Monument includes federal submerged lands within the 3-mile belt off the island of St. John. These waters support a diverse and complex system of coral reefs and other ecosystems such as shoreline mangrove forests and seagrass beds.', 47, 'https://www.nps.gov/vicr/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (440, 'Virgin Islands', 'St. John', 'Go beyond Virgin Islands National Park''s stunning white-sand beaches. Hike to historic plantation sites to learn about a challenging past when sugar and enslaved labor dominated life on the island. Visit the Indigenous Taino''s ancient petroglyphs. Snorkel coral reefs to discover hidden marine life. Two-thirds of the island of St. John is national park, making it a unique destination for visitors.', 47, 'https://www.nps.gov/viis/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (442, 'Voyageurs', 'International Falls', 'With 218,055 acres, Voyageurs National Park is an adventure wonderland all year long full of exposed rock ridges, cliffs, wetlands, forests, streams and lakes. This is a place of transition between land and aquatic ecosystems, between southern boreal and northern hardwood forests, and between wild and developed areas. Whether you are exploring by land, water or ice there is something for everyone.', 24, 'https://www.nps.gov/voya/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (445, 'Waco Mammoth', 'Waco', 'Standing as tall as 14 feet and weighing 20,000 pounds, Columbian mammoths roamed across what is present-day Texas thousands of years ago. Today, the fossil specimens represent the nation''s first and only recorded evidence of a nursery herd of ice age Columbian mammoths.', 45, 'https://www.nps.gov/waco/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (444, 'Walnut Canyon', 'Flagstaff', 'Since time immemorial, Indigenous Peoples have lived and traveled throughout Walnut Canyon’s dynamic landscape. Vibrant communities built their homes in the cliffs and farmed along the canyon’s rim. Today the park preserves this landscape, and the ancestral homes in and around the canyon.', 3, 'https://www.nps.gov/waca/index.htm');
