INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (463, 'Wupatki', 'Flagstaff', 'Nestled between the Painted Desert and ponderosa highlands of northern Arizona, Wupatki National Monument is an unlikely landscape for a thriving community. The early 1100''s marked a time of cooler and wetter weather, when the ancestors of contemporary Pueblo communities created a bustling center of trade and culture. For indigenous peoples, these sites represent the footprints of their ancestors.', 3, 'https://www.nps.gov/wupa/index.htm');
