INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (466, 'Yellowstone', 'Yellowstone National Park', 'On March 1, 1872, Yellowstone became the first national park for all to enjoy the unique hydrothermal wonders. Today, millions of people come here each year to camp, hike, and enjoy the majesty of the park.', 53, 'https://www.nps.gov/yell/index.htm');
