INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (467, 'Yorktown Battlefield', 'Yorktown', 'Discover what it took for the United States to be independent as you explore the site of the last major battle of the Revolutionary War. Here at Yorktown, in the fall of 1781, General George Washington, with allied American and French forces, besieged General Charles Lord Cornwallis’s British army. On October 19, Cornwallis surrendered, effectively ending the war and ensuring independence.', 49, 'https://www.nps.gov/york/index.htm');
