INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (470, 'Yucca House', 'Cortez', 'Yucca House National Monument preserves a large unexcavated pueblo with a stunning setting in Montezuma Valley, nestled between Mesa Verde and Ute Mountain. Since Yucca House was protected as a national monument in 1919, it has remained largely untouched, offering intrepid visitors a sense of discovery and preserving the pueblo''s beauty and integrity for future generations.', 6, 'https://www.nps.gov/yuho/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (469, 'Yukon - Charley Rivers', 'Fairbanks', 'Located in Interior Alaska, Yukon-Charley Rivers offers exploration in a largely untouched landscape. Whether you float the mighty Yukon River or paddle the Charley River''s whitewater, your memories will last a lifetime. Geology, cultural history, gold rush remnants, wildlife, and vast scenery will be a part of your experience. But, the strongest element will be solitude. Your adventure awaits.', 2, 'https://www.nps.gov/yuch/index.htm');
INSERT INTO park (id, name, city, description, state_code_id, url) VALUES (471, 'Zion', 'Springdale', 'Follow the paths where people have walked for thousands of years. Gaze up at massive sandstone cliffs of cream, pink, and red that soar into a brilliant blue sky. Experience wilderness in a narrow slot canyon. Zion’s unique array of plants and animals will enchant you as you absorb the rich history of the past and enjoy the excitement of present-day adventures.', 46, 'https://www.nps.gov/zion/index.htm');
